#include<iostream>#include <fstream>using namespace std;int main(){    char ch; // To hold a character    fstream file("letters.txt", ios::in); // Open "letters.txt" for reading    // Move to the 8th character from the beginning, read, and print it    file.seekg(8L, ios::beg);    file.get(ch);    cout << ch << endl;    // Move to the 4th character from the end, read, and print it    file.seekg(-4L, ios::end);    file.get(ch);    cout << ch << endl;    // Move 3 characters forward from the current position, read, and print it    file.seekg(3L, ios::cur);    file.get(ch);    cout << ch << endl;}